INSERT INTO spk_options VALUES('514', '_transient_dash_4077549d03da2e451c8b5f002294ff51', '<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/02/wordpress-4-1-1/\'>WordPress 4.1.1 Maintenance Release</a> <span class=\"rss-date\">February 18, 2015</span><div class=\"rssSummary\">WordPress 4.1.1 is now available.Â This maintenance release fixes 21 bugs in versionÂ 4.1. Some of you may have been waiting to update to the latest version until now, but there just wasnât much to address. WordPress 4.1Â was a smooth-sailing release and has seen more than 14 million downloads in the last two months. For a full [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/themeconf-a-new-conference-for-designers-themers-and-front-end-developers\'>WPTavern: ThemeConf: A New Conference for Designers, Themers, and Front-End Developers</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/its-time-for-wordpress-to-automatically-update-themes-plugins-and-core-by-default\'>WPTavern: Itâs Time For WordPress to Automatically Update Themes, Plugins, and Core by Default</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/chris-wiegman-former-lead-developer-of-ithemes-security-joins-10up\'>WPTavern: Chris Wiegman, Former Lead Developer of iThemes Security, Joins 10up</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/nextgen-facebook/\' class=\'dashboard-news-plugin-link\'>NextGEN Facebook - Advanced Social SEO for Facebook, Google+, Pinterest, Twitter &amp; More</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=nextgen-facebook&amp;_wpnonce=efe40a3045&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'NextGEN Facebook - Advanced Social SEO for Facebook, Google+, Pinterest, Twitter &amp; More\'>Install</a>)</span></li></ul></div>', 'no');
