INSERT INTO spk_options VALUES('180', '_transient_dash_4077549d03da2e451c8b5f002294ff51', '<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/02/wordpress-4-1-1/\'>WordPress 4.1.1 Maintenance Release</a> <span class=\"rss-date\">February 18, 2015</span><div class=\"rssSummary\">WordPress 4.1.1 is now available.Â This maintenance release fixes 21 bugs in versionÂ 4.1. Some of you may have been waiting to update to the latest version until now, but there just wasnât much to address. WordPress 4.1Â was a smooth-sailing release and has seen more than 14 million downloads in the last two months. For a full [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/wordpress-4-1-1-released-fixes-21-bugs\'>WPTavern: WordPress 4.1.1 Released, Fixes 21 Bugs</a></li><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/02/7-principles-of-rich-web-applications/\'>Matt: 7 Principles of Rich Web Applications</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/customizer-theme-switcher-approved-for-merge-into-wordpress-4-2\'>WPTavern: Customizer Theme Switcher Approved for Merge Into WordPress 4.2</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/nextgen-facebook/\' class=\'dashboard-news-plugin-link\'>NextGEN Facebook - Advanced Social SEO for Facebook, Google+, Pinterest, Twitter &amp; More</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=nextgen-facebook&amp;_wpnonce=c979ab84f5&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'NextGEN Facebook - Advanced Social SEO for Facebook, Google+, Pinterest, Twitter &amp; More\'>Install</a>)</span></li></ul></div>', 'no');
